ns8:component-version-script.script:
msiexec.exe /i "$apache_httpd_installer" /qn SERVERADMIN=$admin_email_addr
# Wait for installation to complete $wait_interval = 5 $wait_limit = 120 $httpd_bin = $apache_root_folder + "\bin\httpd.exe" for ($wait_c=0; $wait_c -le $wait_limit; ) {
Write-Host "Waiting for Apache installation to complete..." if (Test-Path $httpd_bin) { # Final wait for things to settle down Start-Sleep $wait_interval break }
if ($wait_c -ge $wait_limit) { Write-Host "Abort. Apache installation did not finish within $wait_limit seconds." exit 1 }
$wait_c = $wait_c + $wait_interval Start-Sleep $wait_interval }
cd "$apache_root_folder" bin\httpd.exe -k install

