ns8:component-version-script.script:
$fileexist = Test-Path $config_httpd_conf if ($fileexist -eq $false) { Write-Host "Abort. $config_httpd_conf does not exist!" exit 1 }
# Remove existing port configuration (Get-Content $config_httpd_conf) -replace '^Listen(.+)','' | Set-Content $config_httpd_conf
$new_conf_content = "`n`n"
# Enable http port if ($http_port) { $new_conf_content = $new_conf_content + "Listen " + $http_port + "`n" }
# Enable http proxy port if ($http_proxy_port) { $new_conf_content = $new_conf_content + "Listen " + $http_proxy_port + "`n" }
# Enable required httpd modules $new_conf_content = $new_conf_content + ` "LoadModule headers_module modules/mod_headers.so`n" + ` "LoadModule proxy_module modules/mod_proxy.so`n" + ` "LoadModule proxy_ajp_module modules/mod_proxy_ajp.so`n" + ` "LoadModule proxy_balancer_module modules/mod_proxy_balancer.so`n" + ` "LoadModule proxy_connect_module modules/mod_proxy_connect.so`n" + ` "LoadModule proxy_ftp_module modules/mod_proxy_ftp.so`n" + ` "LoadModule proxy_http_module modules/mod_proxy_http.so`n" + ` "LoadModule proxy_scgi_module modules/mod_proxy_scgi.so`n"
# Create virtual host for http port if ($http_port) {
$new_conf_content = $new_conf_content + "`n" + "<VirtualHost *:" + $http_port + ">`n" + ` " DocumentRoot `"" + $config_static_site +"`"`n" + ` " ServerName localhost`n" + ` "</VirtualHost>`n"
}
# Create virtual host for http proxy ports if (! $http_node_ips[0].Equals("None")) {
$SUBSTR = ""
if ($use_ajp.Equals("YES")) {
for ($i=0; $i -lt $http_node_ips.Length; $i++) { $SUBSTR = $SUBSTR + "BalancerMember ajp://" + ` $http_node_ips[$i] + ":" + $http_node_port + "route=" + $appsrv_routes[$i] + "`n" }
$new_conf_content = $new_conf_content + "`n" + "<VirtualHost *:" + $http_proxy_port + ">`n" + ` " <Proxy balancer://mycluster>`n" + ` $SUBSTR + ` " </Proxy>`n" + ` " ProxyPass /" + $ajp_proxy_context +" balancer://mycluster/" + $tomcat_context+ "`n" + ` "</VirtualHost>`n"
} else {
for ($i=0; $i -lt $http_node_ips.Length; $i++) { $SUBSTR = $SUBSTR + "BalancerMember http://" + ` $http_node_ips[$i] + ":" + $http_node_port + " route=" + $appsrv_routes[$i] + "`n" }
$COOKIE_HEADER_DIRECTIVE = "" $STICKY_SESSION_NAME="JSESSIONID`|jsessionid"
if ($autogen_sticky_cookie.Equals("YES")) { $STICKY_SESSION_NAME = "ROUTEID" $COOKIE_HEADER_DIRECTIVE = "Header add Set-Cookie `"ROUTEID=.%{BALANCER_WORKER_ROUTE}e; path=/`" env=BALANCER_ROUTE_CHANGED" }
$new_conf_content = $new_conf_content + "`n" + "<VirtualHost *:" + $http_proxy_port + ">`n" + ` " <Proxy balancer://mycluster>`n" + ` $SUBSTR + ` " </Proxy>`n" + ` " ProxyPass / balancer://mycluster/ stickysession=" + $STICKY_SESSION_NAME + " nofailover=On`n" + ` " ProxyPassReverse / balancer://mycluster/`n" + ` " ProxyPreserveHost On`n" + ` $COOKIE_HEADER_DIRECTIVE + "`n" + ` "</VirtualHost>`n"
} }
# Backup and update httpd config file cd "$apache_root_folder" copy conf\httpd.conf conf\httpd.conf.orig $new_conf_content | Add-Content $config_httpd_conf
# Disable firewall on the desired ports netsh.exe firewall set portopening TCP $http_port "Apache Web Server" netsh.exe firewall set portopening TCP $http_proxy_port "Apache Web Server"

