      #
      # Also note that powershell as is does not care if this variable was a computed 
      # or a string variable that filter happens at a different layer.
      #
      $expandedUserVariable = $ExecutionContext.InvokeCommand.ExpandString($vmw_appd_obj_prop_name_expanded)

     ($vmw_appd_obj_prop_name + '=' + $expandedUserVariable) | Add-Content $($Args[2]) 
 }
}

ns8:component-version-script.script:
$fileexist = Test-Path $config_httpd_conf if ($fileexist -eq $false) { Write-Host "Abort. $config_httpd_conf does not exist!" exit 1 }
# Remove existing port configuration (Get-Content $config_httpd_conf) -replace '^Listen(.+)','' | Set-Content $config_httpd_conf
$new_conf_content = "`n`n"
# Enable http port if ($http_port) { $new_conf_content = $new_conf_content + "Listen " + $http_port + "`n" }
# Enable http proxy port if ($http_proxy_port) { $new_conf_content = $new_conf_content + "Listen " + $http_proxy_port + "`n" }
# Enable required httpd modules $new_conf_content = $new_conf_content + ` "LoadModule headers_module modules/mod_headers.so`n" + ` "LoadModule proxy_module modules/mod_proxy.so`n" + ` "LoadModule proxy_ajp_module modules/mod_proxy_ajp.so`n" + ` "LoadModule proxy_balancer_module modules/mod_proxy_balancer.so`n" + ` "LoadModule proxy_connect_module modules/mod_proxy_connect.so`n" + ` "LoadModule proxy_ftp_module modules/mod_proxy_ftp.so`n" + ` "LoadModule proxy_http_module modules/mod_proxy_http.so`n" + ` "LoadModule proxy_scgi_module modules/mod_proxy_scgi.so`n"
# Create virtual host for http port if ($http_port) {
$new_conf_content = $new_conf_content + "`n" + "<VirtualHost *:" + $http_port + ">`n" + ` " DocumentRoot `"" + $config_static_site +"`"`n" + ` " ServerName localhost`n" + ` "</VirtualHost>`n"
}
# Create virtual host for http proxy ports if (! $http_node_ips[0].Equals("None")) {
$SUBSTR = ""
if ($use_ajp.Equals("YES")) {
for ($i=0; $i -lt $http_node_ips.Length; $i++) { $SUBSTR = $SUBSTR + "BalancerMember ajp://" + ` $http_node_ips[$i] + ":" + $http_node_port + "route=" + $appsrv_routes[$i] + "`n" }
$new_conf_content = $new_conf_content + "`n" + "<VirtualHost *:" + $http_proxy_port + ">`n" + ` " <Proxy balancer://mycluster>`n" + ` $SUBSTR + ` " </Proxy>`n" + ` " ProxyPass /" + $ajp_proxy_context +" balancer://mycluster/" + $tomcat_context+ "`n" + ` "</VirtualHost>`n"
} else {
for ($i=0; $i -lt $http_node_ips.Length; $i++) { $SUBSTR = $SUBSTR + "BalancerMember http://" + ` $http_node_ips[$i] + ":" + $http_node_port + " route=" + $appsrv_routes[$i] + "`n" }
$COOKIE_HEADER_DIRECTIVE = "" $STICKY_SESSION_NAME="JSESSIONID`|jsessionid"
if ($autogen_sticky_cookie.Equals("YES")) { $STICKY_SESSION_NAME = "ROUTEID" $COOKIE_HEADER_DIRECTIVE = "Header add Set-Cookie `"ROUTEID=.%{BALANCER_WORKER_ROUTE}e; path=/`" env=BALANCER_ROUTE_CHANGED" }
$new_conf_content = $new_conf_content + "`n" + "<VirtualHost *:" + $http_proxy_port + ">`n" + ` " <Proxy balancer://mycluster>`n" + ` $SUBSTR + ` " </Proxy>`n" + ` " ProxyPass / balancer://mycluster/ stickysession=" + $STICKY_SESSION_NAME + " nofailover=On`n" + ` " ProxyPassReverse / balancer://mycluster/`n" + ` " ProxyPreserveHost On`n" + ` $COOKIE_HEADER_DIRECTIVE + "`n" + ` "</VirtualHost>`n"
} }
# Backup and update httpd config file cd "$apache_root_folder" copy conf\httpd.conf conf\httpd.conf.orig $new_conf_content | Add-Content $config_httpd_conf
# Disable firewall on the desired ports netsh.exe firewall set portopening TCP $http_port "Apache Web Server" netsh.exe firewall set portopening TCP $http_proxy_port "Apache Web Server"

supported-os-details.exec-script:
$error.clear()
# Auto convert input properties to local Powershell variables

# Parsing JSON elements into Powershell object
function ParseJsonProps ($jsonFile)
{
 $jsonjsonCharCount = 0
 $newObjStart = $false
 $arrayStart = $false
 $tokenStart = $false
 $valueStart = $false
 $escapeStart = $false

 $jsonFileLen = $jsonFile.length
 $jsonObj = New-Object System.Text.StringBuilder

 ForEach($c in $jsonFile.ToCharArray()) {
    $jsonCharCount++
    $tmpStr = ""
    switch -regex ($c) {
        "{" { 
            if($newObjStart -eq $false) {
                $tmpStr = "(New-Object PSObject "
                $newObjStart = $true
            } else {
                $tmpStr = $c
            }
        }
        "}" {
            if($jsonCharCount -lt $jsonFileLen) {
                $tmpStr = $c
            } else {
                $tmpStr = ")"
            }
        }
        '"' {
            if($tokenStart -eq $false -and $valueStart -eq $false -and $arrayStart -eq $false) {
                $tmpStr = '| Add-Member -Passthru NoteProperty "'
            } else {
                if($escapeStart -eq $false) {
                    $tmpStr = $c
                } else {
                    $tmpStr = "```""
                    $escapeStart = $false
                }
            }
            $tokenStart = $true
        }
        ":" {
            if($valueStart -eq $true -or $arrayStart -eq $true) {
                $tmpStr = $c
            } else {
                $tmpStr = " "
            }
            $valueStart = $true
        }
        "," {
            if($arrayStart) {
                $tmpStr = $c
            } else {
                $valueStart = $false
                $tokenStart = $false
            }
        }	
        "\[" {
            $tmpStr = "@("
            $arrayStart = $true
        }
        "\]" {
            $tmpStr = ")"
            $arrayStart = $false
        }
        "``" {
            $tmpStr = "````"
        }
        "\\" {
            if($escapeStart -eq $false) {
                $escapeStart = $true
            } else {
                $tmpStr = $c
                $escapeStart = $false
            }
        }
        "\$" {
            if($escapeStart -eq $false) {
                $tmpStr = '`$'
            } else {
                $tmpStr = '`$'
                $escapeStart = $false
            }
        }
        "n" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``n"
                $escapeStart = $false
            }
        }
        "r" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``r"
                $escapeStart = $false
            }
        }
        "t" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``t"
                $escapeStart = $false
            }
        }
        "b" {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "``b"
                $escapeStart = $false
            }
        }
        default {
            if($escapeStart -eq $false) {
                $tmpStr = $c
            } else {
                $tmpStr = "\$c"
                $escapeStart = $false
            }
            #$tmpStr = $c
            #if($escapeStart -eq $true) {
            #    $escapeStart = $false
            #}
        }
    }                        
    [void] $jsonObj.Append($tmpStr)
 }

 $jsonObj | Invoke-Expression
}

$vmw_appd_json_file = Get-Content "$($Args[1])"
$vmw_appd_json_obj = ParseJsonProps $vmw_appd_json_file
$vmw_appd_json_props = $vmw_appd_json_obj.PsObject.Properties

# Assigning obj to local variable
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 #$vmw_appd_obj_prop_name_full = '$vmw_appd_json_obj' + '.' + $vmw_appd_obj_prop_name
 #$vmw_appd_obj_prop_value = Invoke-Expression $vmw_appd_obj_prop_name_full
 Set-Variable -Name $vmw_appd_obj_prop_name -value $vmw_appd_json_obj."$vmw_appd_obj_prop_name"
}


# Execute user's Powershell script
$error.clear()
$LASTEXITCODE = 0
. "$($Args[0])"

if ($LASTEXITCODE -ne 0) {
 Write-Host "`nABORT. LASTEXITCODE is non-zero.`n"
 exit $LASTEXITCODE
}

if ($error.count -gt 0) {
 Write-Host "`nABORT. Encountered error in Powershell.`n"
 exit $error.count
 }

# Export Powershell variables to output properties file
ForEach ($vmw_appd_obj_prop in $vmw_appd_json_props)
{
 $vmw_appd_obj_prop_name = $vmw_appd_obj_prop.Name;
 $vmw_appd_obj_prop_type = (($vmw_appd_json_obj.($vmw_appd_obj_prop_name)).GetType()).Name

 # Export only String AppD's properties; Output Array properties is not currently supported.
 if ($vmw_appd_obj_prop_type.Contains("String")) {
      $vmw_appd_obj_prop_name_expanded = '$' + $vmw_appd_obj_prop_name

      # The ExpandString takes care of three cases. 
      # Case-I when the string is set via input properties
      # Case-II when the string is set via input properties and overridden in script (install/configure..)
      # Case-III lastly when it is only set via script (install/configure..) and left empty in input props
      #
      # Also note that powershell as is does not care if this variable was a computed 
      # or a string variable that filter happens at a different layer.
      #
      $expandedUserVariable = $ExecutionContext.InvokeCommand.ExpandString($vmw_appd_obj_prop_name_expanded)

     ($vmw_appd_obj_prop_name + '=' + $expandedUserVariable) | Add-Content $($Args[2]) 
 }
}

$error.clear()
